package cn.bupt.john.hash;

import java.util.HashMap;

/**
 * 解题思路
 * <p>
 *     使用HashMap保存s的结果集，key为字符，value为出现次数
 *     遍历t，
 *     <li>
 *         如果HashMap中不存在该值，直接返回。
 *     </li>
 *     <li>
 *         如果hashMap中存在, 则使其值减一，如果减后值为0，则remove
 *     </li>
 * </p>
 *
 * <p>
 *      使用位比较，异或操作。
 * </p>
 */
public class P389_FindTheDifference {

    public static void main(String[] args) {
        P389_FindTheDifference solution = new P389_FindTheDifference();
        System.out.println(solution.findTheDifference("", ""));
    }

    public char findTheDifference(String s, String t) {
        char[] sChar = s.toCharArray();
        char[] tChar = t.toCharArray();

        HashMap<Character, Integer> result = new HashMap<>();
        for (int i = 0; i < sChar.length; i++) {
            if (!result.containsKey(sChar[i])) {
                result.put(sChar[i], 1);
            }
            else {
                result.put(sChar[i], result.get(sChar[i]) + 1);
            }
        }
        for (int i = 0; i < tChar.length; i++) {
            if (!result.containsKey(tChar[i])) {
                return tChar[i];
            }
            else {
                Integer get = result.get(tChar[i]);
                if (--get == 0) {
                    result.remove(tChar[i]);
                }
                else {
                    result.put(tChar[i], get);
                }
            }
        }
        return (char) result.keySet().toArray()[0];
    }
}
